  body {
    font-family: Arial, Helvetica, sans-serif;
    font-weight: 400;
    /*color: rgba(255, 255, 255, .5);*/
    font-family: android;

  }

  * {
    padding: 0;
    margin: 0;
    box-sizing: border-box;
  }

  .container {
    color: #fff;
    height: 100vh;
    background: #201028;
    padding: 1rem;
    position: relative;
  }



  #output-controls {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 .25rem;

  }



  /*style the app title and subtitle */
  #hero {
    text-align: center;
    margin: 0 2.75rem;
    padding: 0 1.75rem;
  }

  #hero h1 {
    font-weight: 300;
    font-family: android;
  

  }

  #hero div {
    color: #fff;
    padding: .5rem 1rem;
  }

  #hero {
    display: flex;
    flex-direction: column;
    justify-content: space-between;

  }

  #whatsapp-layer {
    position: relative !important;
    margin-top: 15vh;
  }

  #whatsapp-layer input {
    width: 100%;
    height: 2.5rem;
    border: none;
    padding: 0 5%;
    border-bottom: #fff solid .1px;
    text-align: center;
    outline: none;
    background: transparent;
    color: #fff;

  }

  #whatsapp-layer div {
    font-size: 70%;
    color: #fff;

  }


  #divider {
    margin-top: 40vh;
    color: #fff;

  }

  #output-controls {
    position: fixed;
    left: 0;
    bottom: 0;
    background: #f5f5f5;
    width: 100%;
    height: 7.5vh;
    padding: 0 .75rem;
    border-radius: 20px 20px 0 0;
  }

  #output-controls img:hover {
    background: rgba(244, 56, 120, .01);
    transform: scale(1.45);
    transition: all .2s cubic-bezier(0.23, 1, 0.320, 1);
  }

  #charLength {
    font-size: 70%;
    font-style: italic;
  }
  
  .slit-in-horizontal{-webkit-animation:slit-in-horizontal .45s ease-out both;animation:slit-in-horizontal .45s ease-out both}
  
  
  
  
