    #init {
      width: 10vh;
      height: 10vh;
      border-radius: 50%;
      border: .45rem solid rgba(255, 255, 255, .5);
      border-top: .45rem #fff solid;
      animation: fade-in, 2s linear .5s, spin 2s ease-out infinite .5s;
      position: absolute;
      top: calc(40vh);
      left: calc(40vw);
      background: transparent;
    }



    #menu {
      display: none;
      flex-direction: column;
      position: absolute;
      top: 23;
      z-index: 1000;
    }

    #menu img {
      width: 35px;
      fill: #fff;
      padding: .25rem;
      border-radius: 5px;
      cursor: pointer;
    }

    #menu img:hover {
      transform: scale(1.25);
      transition: all .2s cubic-bezier(0.23, 1, 0.320, 1);
      border-radius: 7px;
    }

    .dis {
      display: block;
    }



    .swing-in-top-bck {
      animation: swing-in-top-bck .5s cubic-bezier(.175, .885, .32, 1.275) both;
      animation-delay: 2s;

    }

    .active-layer {
      -webkit-animation: active-layer 3.5s ease-in-out 1 both;
      animation: active-layer 3.5s ease-in-out infinite both;
    }


    .scale-in-center {
